import React, { Component } from 'react'

var allPostings = [
    ["Item 1 ID", "Item 1 Title", "Item 1 Description", "Item 1 Seller", "$Item 1 Price"],
    ["Item 2 ID", "Item 2 Title", "Item 2 Description", "Item 2 Seller", "$Item 2 Price"],
    ["Item 3 ID", "Item 3 Title", "Item 3 Description", "Item 3 Seller", "$Item 3 Price"],
    ["Item 4 ID", "Item 4 Title", "Item 4 Description", "Item 4 Seller", "$Item 4 Price"]
];

class MarketComponent extends Component {
    render() {
        let retVal = (
            <div>
                <h1>Browse Marketplace</h1>
                <div className="container">
                    <Items></Items>
                </div>
            </div>
        );
        return retVal;
    }


}

class Items extends Component {
    render() {
        let retVal;
        var r;
        for (r in allPostings) {
            retVal += (
                <div className="container">
                    <img src={r[0] + '.jpg'} height='50px'></img><span>{r[1]}</span> <br></br>
                    <span>{r[1]}</span> <br></br>
                    <span>{r[2]}</span> <br></br>
                    <span>{r[3]}</span> <br></br>
                    <br></br>
                </div>
            )
        }


        return retVal;
    }
}



export default MarketComponent
